'use strict';

var agGridCommunity = require('ag-grid-community');
var buildIconUrl = require('../utils/build-icon-url.cjs');

/** Map of AG Grid icon names to their corresponding PDS icons */
const agGridToPdsIconMap = {
    aggregation: 'aggregation',
    arrows: 'arrows',
    asc: 'arrow-up',
    cancel: 'close',
    chart: 'chart',
    'color-picker': 'color-picker',
    columns: 'grid',
    contracted: 'arrow-head-right',
    copy: 'copy',
    cross: 'close',
    csv: 'file-csv',
    cut: 'cut',
    desc: 'arrow-down',
    down: 'arrow-down',
    expanded: 'arrow-head-left',
    excel: 'file-excel',
    eye: 'view',
    'eye-slash': 'view-off',
    filter: 'filter',
    first: 'arrow-first',
    grip: 'grip',
    group: 'group',
    last: 'arrow-last',
    left: 'arrow-left',
    linked: 'linked',
    maximize: 'zoom-out',
    menu: 'menu-lines',
    'menu-alt': 'menu-dots-vertical',
    minimize: 'zoom-in',
    minus: 'minus',
    next: 'arrow-head-right',
    'not-allowed': 'disable',
    none: 'list',
    paste: 'paste',
    pin: 'push-pin',
    pivot: 'pivot',
    plus: 'plus',
    previous: 'arrow-head-left',
    right: 'arrow-right',
    save: 'save',
    settings: 'configurate',
    'small-down': 'arrow-compact-down',
    'small-left': 'arrow-compact-left',
    'small-right': 'arrow-compact-right',
    'small-up': 'arrow-compact-up',
    tick: 'check',
    'tree-closed': 'arrow-head-right',
    'tree-indeterminate': 'minus',
    'tree-open': 'arrow-head-down',
    unlinked: 'unlinked',
    up: 'arrow-up',
};
const pdsSvgIcons = agGridCommunity.iconOverrides({
    type: 'image',
    mask: true,
    icons: {
        ...Object.fromEntries(Object.entries(agGridToPdsIconMap).map(([key, value]) => [key, { url: buildIconUrl.buildIconUrl(value) }])),
    },
});

exports.pdsSvgIcons = pdsSvgIcons;
