'use strict';

var index = require('../assets/projects/icons/dist/index.cjs');
var getCdnBaseUrl = require('./get-cdn-base-url.cjs');

const DEFAULT_ICON_NAME = 'question';
const buildIconUrl = (iconName) => {
    const iconFileName = index.ICONS_MANIFEST[iconName];
    return iconFileName ? `${getCdnBaseUrl.getCDNBaseURL()}/icons/${iconFileName}` : buildIconUrl(DEFAULT_ICON_NAME);
};

exports.buildIconUrl = buildIconUrl;
