import { LatLong } from './places';
export interface PPNDealerSearchResult {
  ppnDealer: PPNDealer;
  distance?: PPNDistance;
}
export interface PPNDealer {
  id: string;
  name: string;
  nameLocalized: string;
  address: Address;
  addressLocalized: AddressLocalized;
  contactDetails: ContactDetails;
  coordinates: LatLong;
  porschePartnerNo: string;
  ppnOrgId: string;
  market?: string;
}
export interface PPNDistance {
  value: number;
  unit: string;
}
export interface Address {
  street: string;
  postalCode: string;
  city: string;
  state: string;
  countryCode: string;
}
export interface AddressLocalized {
  street?: string;
  city?: string;
  state?: string;
}
export interface ContactDetails {
  phoneNumber: string;
  emailAddress: string;
  homepage: string;
  contactOpeningHours: OpenHours[];
}
export interface City {
  cityName: string;
  cityNameLocalized?: string | null;
  dealers: Record<string, {
    ppnDealer: PPNDealer;
  }>;
}
export interface DealersByRegion {
  regions: {
    regionName: string;
    regionNameLocalized?: string | null;
    cities: City[];
  }[];
}
export interface OpenHours {
  day: string;
  open: string;
  close: string;
}
